function onSubmit() {
    //Type appropriate comment here, and begin script below
    /**check start date/time later than submission time and later then end date/time**/
    var now = new Date();
    var start = getDateFromFormat(g_form.getValue("start_date_time"), g_user_date_time_format);
    var end = getDateFromFormat(g_form.getValue("end_date_time"), g_user_date_time_format);

    if (start <= now) {
        alert("start date/time before now. current time: " + now + "start at: " + start);
        g_form.addErrorMessage("Start Date must be after current time.");
        return false;
    } else if (start >= end) {
        alert("start later than endã€‚ start at " + start + " end at " + end);
        g_form.addErrorMessage("Start Time must be after End Time.");
        return false;
    }
}
