sys_script_client.script:
function onSubmit() {
    //Type appropriate comment here, and begin script below
    var now = new Date();
    var start = getDateFromFormat(g_form.getValue("start_date_time"), g_user_date_time_format);
	var end = getDateFromFormat(g_form.getValue("end_date_time"), g_user_date_time_format);

    if (start <= now) {
		alert("start date/time mus be after current time.");
        g_form.addErrorMessage("Start Date must be after current time.");
        return false;
    }else if (start >= end) {
		alert("start date/time must be before end date/time");
        g_form.addErrorMessage("Start Time must be after End Time.");
        return false;
    }

}
