sys_script_client.script:
function onSubmit() {
	//calculate duration by end - start
	var start = getDateFromFormat(g_form.getValue("start_date_time"), g_user_date_time_format);
    var end = getDateFromFormat(g_form.getValue("end_date_time"), g_user_date_time_format);
	var childNum = g_form.getValue("children_number");
	var duration = end - start;
	var hr=(duration/3600000).toFixed(2);//round to 2 decimal,calculated rounded hour
	//we charge $15/hour per child. calculate the toal payment is: hr * childNum * 15.
	var totalCost = (15 * hr * childNum);
	var discount = 0;
	if(childNum > 3){ // if more than 3 children 20% discount
		discount = totalCost * 0.2;	
	}else if(childNum > 1) {// if more than 1 child 10% discount
		discount = totalCost * 0.1;	
	}
	totalCost -= discount;
	//g_form.addInfoMessage("start: " + start + " .end: " + end + ". duration: " + duration + ", " + " children number: " + childNum + '. hr: '  + hr +  ". total cost: $" + totalCost + " discount: $" + discount);

	var answer = confirm("Your total payment amount will be $" + totalCost + "\nYou saved: $" + discount +"\n\nDo you want to confirm your request?");
	if(answer == true){
		g_form.addInfoMessage(g_user.getFullName() + " confirmed a babysitter request. $" + totalCost + " will be deducted from your next paycheck.");
		return true;
	}else{
		g_form.addInfoMessage((g_user.getFullName() + " did not confirm a babysitter request"));
		return false;
	}

}
